using Newtonsoft.Json.Linq;using WeatherApp;string key = File.ReadAllText("appsettings.json");string APIKey = JObject.Parse(key).GetValue("APIKey").ToString();Console.WriteLine("Please enter your zip code");var zipCode = Console.ReadLine();var apiCall = $"https://api.openweathermap.org/data/2.5/weather?zip={zipCode}&units=imperial&appid={APIKey}";Console.WriteLine($"");Console.WriteLine($"In {WeatherMap.GetCity(apiCall)} the current temperature is {WeatherMap.GetTemp(apiCall)}"); 