using System;using Newtonsoft.Json.Linq;namespace WeatherApp{    public class WeatherMap    {        public static double GetTemp(string apiCall)        {            var client = new HttpClient();            var res = client.GetStringAsync(apiCall).Result;            var temp = double.Parse(JObject.Parse(res)["main"]["temp"].ToString());            return temp;        }        public static string GetCity(string apiCall)        {            var client = new HttpClient();            var res =  client.GetStringAsync(apiCall).Result;            var city = JObject.Parse(res)["city"].ToString();            return city;        }    }}